tinymce.PluginManager.add('bootgrid', function (editor, url) {
    //setup

    //path to plugin
    var path = tinymce.baseURL + '/plugins/bootgrid/';
    var iconpath = path + 'bootgrid-menu-icon.png';

    editor.addMenuItem('bootgrid-4-4', {
        image: iconpath,
        text: 'Bootgrid |-|-|-|-| columns',
        context: 'tools',
        onclick: function () {
            //add some html!
            var html =  '<div class="row">' +
		                    '<div class="col-md-3">Col 1</div>' +
		                    '<div class="col-md-3">Col 2</div>' +
		                    '<div class="col-md-3">Col 3</div>' +
		                    '<div class="col-md-3">Col 4</div>' +
	                    '</div><p>&nbsp;</p>';
            editor.insertContent(html);
        }
    });

    editor.addMenuItem('bootgrid-8-4', {
        image: iconpath,
        text: 'Bootgrid |--|-| columns',
        context: 'tools',
        onclick: function () {
            var html = '<div class="row">' +
		                    '<div class="col-md-8">Col 1</div>' +
		                    '<div class="col-md-4">Col 2</div>' +
	                    '</div><p>&nbsp;</p>';
            editor.insertContent(html);
        }
    });

    editor.addMenuItem('bootgrid-4-8', {
        image: iconpath,
        text: 'Bootgrid |-|--| columns',
        context: 'tools',
        onclick: function () {
            var html = '<div class="row">' +
		                    '<div class="col-md-4">Col 1</div>' +
		                    '<div class="col-md-8">Col 2</div>' +
	                    '</div><p>&nbsp;</p>';
            editor.insertContent(html);
        }
    });

    editor.addMenuItem('bootgrid-6-6', {
        image: iconpath,
        text: 'Bootgrid |--|--| columns',
        context: 'tools',
        onclick: function () {
            var html = '<div class="row">' +
		                    '<div class="col-md-6">Col 1</div>' +
		                    '<div class="col-md-6">Col 2</div>' +
	                    '</div><p>&nbsp;</p>';
            editor.insertContent(html);
        }
    });

    editor.addMenuItem('bootgrid-3-9', {
        image: iconpath,
        text: 'Bootgrid |-|---| columns',
        context: 'tools',
        onclick: function () {
            var html = '<div class="row">' +
		                    '<div class="col-md-3">Col 1</div>' +
		                    '<div class="col-md-9">Col 2</div>' +
	                    '</div><p>&nbsp;</p>';
            editor.insertContent(html);
        }
    });

    editor.addMenuItem('bootgrid-9-3', {
        image: iconpath,
        text: 'Bootgrid |---|-| columns',
        context: 'tools',
        onclick: function () {
            var html = '<div class="row">' +
		                    '<div class="col-md-9">Col 1</div>' +
		                    '<div class="col-md-3">Col 2</div>' +
	                    '</div><p>&nbsp;</p>';
            editor.insertContent(html);
        }
    });

    editor.addMenuItem('bootgrid-12', {
        image: iconpath,
        text: 'Bootgrid |----| columns',
        context: 'tools',
        onclick: function () {
            var html = '<div class="row">' +
		                    '<div class="col-md-12">Col 1</div>' +
	                    '</div><p>&nbsp;</p>';
            editor.insertContent(html);
        }
    });
});